import React, {useState, useEffect, Fragment} from 'react';
import Table from 'react-bootstrap/Table';
import 'bootstrap/dist/css/bootstrap.css';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const Employee_CRUD=()=>{

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const personData=[
        {
        Person_Id:'1',
        First_Name:'Syed',
        Last_Name:'Fraz',
        Email:'frazpk15@gmail.com',
        Contact:'03314466033',
        DOB:'2003-03-22',
        Gender:true,
        CNIC:'35201-3825804-1'
        }
    ]
    
    const handleDelete=(Person_Id)=>{
        if(window.confirm("Are you sure you want to delete this employee")){
            alert(Person_Id);
        }
        handleShow
    }
    const handleEdit=(Person_Id)=>{
        alert(Person_Id)
    }
    const [data, setData]=useState([]);

    useState(()=>{
        setData(personData);
    },{})
  

    return (
        <Fragment>
        <Table striped bordered hover>
            <thead>
                <tr>
                <th>#</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Contact</th>
                <th>DOB</th>
                <th>Gender</th>
                <th>CNIC</th>
                </tr>
            </thead>
            <tbody>

                {
                    data && data.length > 0 ?
                        data.map((item, index)=>{
                            return(
                                <tr key={index}>
                                    <td>{index+1}</td>
                                    <td>{item.First_Name}</td>
                                    <td>{item.Last_Name}</td>
                                    <td>{item.Email}</td>
                                    <td>{item.Contact}</td>
                                    <td>{item.DOB}</td>
                                    <td>{item.Gender}</td>
                                    <td>{item.CNIC}</td>
                                    <td colSpan={2}>
                                        <button className="btn btn-primary" onClick={()=> handleEdit(item.Person_Id)}>Edit</button> &nbsp;
                                        <button className="btn btn-danger" onClick={()=> handleDelete(item.Person_Id)}>Delete</button> &nbsp;

                                    </td>
                                </tr>
                            ) 
                    })
                    :
                    'Loading....'


                }
                
                
            </tbody>
            </Table>
        </Fragment>
    )
}

export default Employee_CRUD;
